document.querySelectorAll(".prnt").forEach(function(element,i,arr){element.classList.replace("prnt","print");element.classList.remove("d-none");element.classList.remove("disabled");if(element.dataset.href||element.href){element.addEventListener("click",printElement)}});function printElement(){this.classList.add("disabled");event.preventDefault();document.querySelector("#modal .spinner-border")?.classList.remove("d-none");const request=new XMLHttpRequest;let url=null;if(this.href){url=this.href}else if(this.dataset.href){url=this.dataset.href}if(!url){return}request.open("GET",url);request.setRequestHeader("X-Requested-With","XMLHttpRequest");request.addEventListener("readystatechange",function(evemnt){if(request.readyState===4&&request.status===200){const prnt=document.getElementById("prnt");if(prnt){prnt.innerHTML=request.responseText;let myModalEl=document.querySelector("#modal");if(myModalEl){setTimeout(function(){let modal=bootstrap.Modal.getOrCreateInstance(myModalEl);modal.hide();myModalEl.addEventListener("hidden.bs.modal",printers)},500)}}}});request.send();this.classList.remove("disabled");return false}function printers(){window.print();document.getElementById("prnt").innerHTML="";let myModalEl=document.querySelector("#modal");myModalEl.removeEventListener("hidden.bs.modal",printers)}