document.querySelectorAll(".group-role-checked").forEach(function(check){check.addEventListener("change",function(){let role=this;let change=document.getElementById("voter_"+role.id);if(role.checked===true){change.classList.remove("d-none");change.classList.add("d-block")}else{change.classList.add("d-none");change.classList.remove("d-block")}change.querySelectorAll("input[type=checkbox]").forEach(function(voter){voter.checked=role.checked})})});