var $addButton=document.getElementById("section_addCollection");let $blockCollection=document.getElementById("section_collection");if($blockCollection){let $delItem=$blockCollection.querySelectorAll(".del-item-section");if($delItem){$delItem.forEach(function(item,i,arr){item.addEventListener("click",function(event){let $counter=$addButton.dataset.index;item.closest(".item-collection-section").remove()})})}let $addButtonField=document.querySelectorAll('[id^="field_addCollection_"]');$addButtonField.forEach(function(item,i,arr){fieldCollection(item.dataset.section)});let $delItemField=$blockCollection.querySelectorAll(".del-item-field");$delItemField.forEach(function(item,i,arr){item.addEventListener("click",function(event){let $countField=item.closest(".item-collection-field").parentNode.getElementsByClassName("item-collection-field").length;if($countField>1){item.closest(".item-collection-field").remove()}else{alert("Минимально должно быть добавлено одно поле")}})});$addButton.addEventListener("click",function(event){let $addButton=this;let newForm=$addButton.dataset.prototype;let index=$addButton.dataset.index*1;newForm=newForm.replace(/__section__/g,index);let div=document.createElement("div");div.classList.add("card");div.classList.add("card-flush");div.classList.add("p-4");div.classList.add("mb-4");div.classList.add("item-collection-section");div.innerHTML=newForm;$blockCollection.append(div);$addButton.dataset.index=(index+1).toString();div.querySelector(".del-item-section").addEventListener("click",function(event){let $counter=$blockCollection.getElementsByClassName("item-collection-section").length;if($counter>1){$addButton.dataset.index=($addButton.dataset.index-1).toString();this.closest(".item-collection-section").remove()}else{alert("Минимально должна быть добавлена одна секция")}});fieldCollection(index);let $delField=div.querySelector(".del-item-field");if($delField){$delField.addEventListener("click",function(event){this.closest(".item-collection-field").remove()})}})}function fieldCollection(index){var $addButtonFields=document.getElementById("field_addCollection_"+index);$addButtonFields.addEventListener("click",function(event){createField($addButtonFields)})}function createField($addButtonFields){let newFormField=$addButtonFields.dataset.prototype;let index=$addButtonFields.dataset.index*1;let section=$addButtonFields.dataset.section*1;let $blockCollectionFields=document.getElementById("field_collection_"+section);newFormField=newFormField.replace(/__section__/g,section);newFormField=newFormField.replace(/__field__/g,index);let div=document.createElement("div");div.classList.add("item-collection-field");div.classList.add("card");div.classList.add("card-body");div.classList.add("border-light");div.innerHTML=newFormField;$blockCollectionFields.append(div);$addButtonFields.dataset.index=(index+1).toString();div.querySelector(".del-item-field").addEventListener("click",function(event){let $countField=div.parentNode.getElementsByClassName("item-collection-field").length;if($countField>1){$addButtonFields.dataset.index=($addButtonFields.dataset.index-1).toString();div.remove()}else{alert("Минимально должно быть добавлено одно поле")}})}